INSERT INTO `hn_note` VALUES ('0c134ea5', 'Springboot 异步线程任务配置', '<h3>\n	一、在启动类上添加注解，开启异步任务\n	<hr />\n</h3>\n<p>\n	&nbsp;&nbsp;&nbsp; 添加注解：<span style=\"color:#EE33EE;\"><strong>@EnableAsync</strong></span>\n</p>\n<pre class=\"prettyprint lang-java\">import org.springframework.boot.SpringApplication;\nimport org.springframework.boot.autoconfigure.SpringBootApplication;\nimport org.springframework.scheduling.annotation.EnableAsync;\n\n/**\n * 系统启动接口\n * @author Horgn黄小锤\n * @date 2019年1月30日 下午5:18:56\n * @version V1.0\n */\n@EnableAsync//启动异步线程任务\n@SpringBootApplication\npublic class Application {\n	\n	public static void main(String[] args) {\n		SpringApplication.run(Application.class, args);\n	}\n	\n}</pre>\n<h3>\n	二、添加配置类，配置异步任务线程池\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-java\">import org.springframework.context.annotation.Bean;\nimport org.springframework.core.task.TaskExecutor;\nimport org.springframework.scheduling.concurrent.ThreadPoolTaskExecutor;\nimport org.springframework.stereotype.Component;\n\n/**\n * 异步线程任务配置\n * @author Horgn黄小锤\n * @date 2019年2月22日 上午11:01:03\n * @version V1.0\n */\n@Component\npublic class ThreadConfig {\n	\n	@Bean\n	public TaskExecutor getTaskExecutor() {\n		ThreadPoolTaskExecutor taskExecutor = new ThreadPoolTaskExecutor();\n		taskExecutor.setMaxPoolSize(20);//最大线程数\n		taskExecutor.setCorePoolSize(10);//线程池的基本大小\n		taskExecutor.setQueueCapacity(2000);//最大队列长度\n		return taskExecutor;\n	}\n}</pre>\n<h3>\n	三、在Service或Dao中添加执行方法，并在实现类对应的方法中添加注解\n	<hr />\n</h3>\n<p>\n	1）方法创建\n</p>\n<pre class=\"prettyprint lang-java\">/**\n * 异步增加笔记访问量（+1）\n * @author Horgn黄小锤\n * @date 2019年2月22日 上午11:17:30\n * @param sNoteId\n */\nvoid asyncAddReadCount(String sNoteId);</pre>\n<p>\n	2）方法实现\n</p>\n<p>\n	&nbsp;&nbsp;&nbsp; 添加注解：<span style=\"color:#EE33EE;\"><strong>@Async</strong></span>\n</p>\n<pre class=\"prettyprint lang-java\">@Override\n@Async\npublic void asyncAddReadCount(String sNoteId) {\n	\n	if(StringUtils.isEmpty(sNoteId)){\n		return;\n	}\n	\n	Lock lock = new ReentrantLock();// 锁对象\n	lock.lock();//获得锁\n	\n	try {\n		String sql = \" UPDATE HN_Note SET iReadCount=iReadCount+1 WHERE sId=\'\"+sNoteId+\"\' AND iState=1 \";\n		super.updateBySql(sql);\n	} finally {\n		lock.unlock();//释放锁\n	}\n}</pre>\n<h3>\n	四、调用方法\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-js\">dao.asyncAddReadCount(sId);</pre>\n<p>\n	<br />\n</p>', '', '2', '3be7f227', '17', null, 'admin001', '2019-02-22 11:56:50', 'admin001', '2019-02-22 17:07:10', '1', '99', '5');
