INSERT INTO `hn_note` VALUES ('0d700567', 'window系统telnet连接linux系统', '<p style=\"font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">1</span><span style=\"font-family:微软雅黑;\">、在程序设置中开启</span><span style=\"font-family:Calibri;\">telnet</span><span style=\"font-family:微软雅黑;\">服务</span> \n</p>\n<p style=\"font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">2</span><span style=\"font-family:微软雅黑;\">、在</span><span style=\"font-family:Calibri;\">linux</span><span style=\"font-family:微软雅黑;\">中安装并开启</span><span style=\"font-family:Calibri;\">telnet</span> \n</p>\n<p style=\"font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">3</span><span style=\"font-family:微软雅黑;\">、连接系统</span> \n</p>\n<p style=\"margin-left:.375in;font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">1</span><span style=\"font-family:微软雅黑;\">）打开</span><span style=\"font-family:Calibri;\">cmd</span> \n</p>\n<p style=\"margin-left:.375in;font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">2</span><span style=\"font-family:微软雅黑;\">）输入</span><span style=\"font-family:Calibri;\">telnet</span><span style=\"font-family:微软雅黑;\">，打开</span><span style=\"font-family:Calibri;\">telnet</span> \n</p>\n<p style=\"margin-left:.375in;font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">3</span><span style=\"font-family:微软雅黑;\">）输入命令</span><span style=\"font-family:Calibri;\"> open ip</span><span style=\"font-family:微软雅黑;\">地址</span><span style=\"font-family:Calibri;\"> </span><span style=\"font-family:微软雅黑;\">端口</span> \n</p>\n<p style=\"margin-left:.375in;font-size:10.5pt;\">\n	<span style=\"font-family:Calibri;\">4</span><span style=\"font-family:微软雅黑;\">）输入远程系账号密码</span> \n</p>', '', '2', 'f5e3e654', '0', null, 'admin001', '2019-02-19 18:23:50', null, null, '1', '1', '1');
INSERT INTO `hn_note` VALUES ('0eb18a99', 'MD5 加密', '<h3>\n	使用API自带的java.security.MessageDigest;进行加密\n</h3>\n<p>\n	<br />\n</p>\n<pre class=\"prettyprint lang-js\">/**\n * 生成MD5\n * @author Horgn黄小锤\n * @param message\n * @return\n * @date 2018-03-19 11:56:26\n */\n\n    private String getMD5(String message) { \n        String md5 = \"\"; \n        try { \n            MessageDigest md = MessageDigest.getInstance(\"MD5\");  // 创建一个md5算法对象 \n            byte[] messageByte = message.getBytes(\"UTF-8\"); \n            byte[] md5Byte = md.digest(messageByte);              // 获得MD5字节数组,16*8=128位 \n            md5 = bytesToHex(md5Byte);                            // 转换为16进制字符串 \n        } catch (Exception e) { \n            e.printStackTrace(); \n        } \n        return md5; \n    } \n  \n    /**\n     * 二进制转十六进制 \n     * @author Horgn黄小锤\n     * @param bytes\n     * @return\n     * @date 2018-03-19 11:56:34\n     */\n    private String bytesToHex(byte[] bytes) { \n        StringBuffer hexStr = new StringBuffer(); \n        int num; \n        for (int i = 0; i &lt; bytes.length; i++) { \n            num = bytes[i]; \n             if(num &lt; 0) { \n                 num += 256; \n            } \n            if(num &lt; 16){ \n                hexStr.append(\"0\"); \n            } \n            hexStr.append(Integer.toHexString(num)); \n        } \n        return hexStr.toString().toUpperCase(); \n    } </pre>\n<p>\n	<br />\n</p>', '', '2', '86cd00d8', '15', null, 'admin001', '2019-02-14 12:39:02', 'admin001', '2019-02-22 19:02:11', '1', '1', '3');
