INSERT INTO `hn_note` VALUES ('1c73962f', 'Oracle 连接信息', '<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"907\" height=\"367\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span><span style=\"font-weight:bold;\">、</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">Oracle </span><span style=\"font-weight:bold;\">连接信息</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;dependency&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;groupId&gt;</span><span style=\"font-weight:bold;\">com.oracle</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/groupId&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;artifactId&gt;</span><span style=\"font-weight:bold;\">ojdbc6</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/artifactId&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;version&gt;</span><span style=\"font-weight:bold;\">11.2.0.0</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/version&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF