INSERT INTO `hn_note` VALUES ('3c3f3a60', 'MyBaits 获取自增长ID', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"976\" height=\"146\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;color:#333333;\">在</span><span style=\"font-family:Arial;color:#333333;\">Mybatis\n  Mapper</span><span style=\"font-family:微软雅黑;color:#333333;\">文件中添加属性“</span><span style=\"font-family:Arial;color:#333333;\">useGeneratedKeys</span><span style=\"font-family:微软雅黑;color:#333333;\">”和“</span><span style=\"font-family:Arial;color:#333333;\">keyProperty</span><span style=\"font-family:微软雅黑;color:#333333;\">”，其中</span><span style=\"font-weight:bold;font-family:Arial;color:red;\">keyProperty</span><span style=\"font-weight:bold;font-family:微软雅黑;color:red;\">是</span><span style=\"font-weight:bold;font-family:Arial;color:red;\">Java</span><span style=\"font-weight:bold;font-family:微软雅黑;color:red;\">对象的属性名，而不是表格的字段名</span><span style=\"font-family:微软雅黑;color:#333333;\">。</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;insert&nbsp;id=\"insert\"&nbsp;parameterType=\"Spares\"&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-weight:bold;\">&nbsp;useGeneratedKeys=\"true\"&nbsp;keyProperty=\"id\"</span>&gt;&nbsp;&nbsp;&nbsp;&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span>insert</span><span>&nbsp;</span><span>into</span><span>&nbsp;</span><span>system(name)</span><span>&nbsp;</span><span>values(#{name})</span><span>&nbsp;&nbsp;&nbsp;&nbsp;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/insert&gt;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"975\" height=\"94\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;color:#333333;\">\n						<span style=\"font-family:Arial;\">Mybatis</span><span style=\"font-family:微软雅黑;\">执行完插入语句后，自动将自增长值赋值给对象</span><span style=\"font-family:Arial;\">systemBean</span><span style=\"font-family:微软雅黑;\">的属性</span><span style=\"font-family:Arial;\">id</span><span style=\"font-family:微软雅黑;\">。因此，可通过</span><span style=\"font-family:Arial;\">systemBean</span><span style=\"font-family:微软雅黑;\">对应的</span><span style=\"font-family:Arial;\">getter</span><span style=\"font-family:微软雅黑;\">方法获取！</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>int&nbsp;</span><span>count</span><span>&nbsp;</span><span>=</span><span>&nbsp;</span><span>systemService.insert(systemBean);</span><span>&nbsp;&nbsp;&nbsp;&nbsp;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>int&nbsp;</span><span>id</span><span>&nbsp;</span><span>=</span><span>&nbsp;</span><span>systemBean.getId();</span><span>&nbsp;//获取到的即为新插入记录的ID&nbsp;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"979\" height=\"95\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						<span style=\"font-weight:bold;\">注意事项</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;color:#333333;\">\n						<span style=\"font-family:Arial;\">1.Mybatis Mapper </span><span style=\"font-family:微软雅黑;\">文件中，“</span><span style=\"font-family:Arial;\">useGeneratedKeys</span><span style=\"font-family:微软雅黑;\">”和“</span><span style=\"font-family:Arial;\">keyProperty</span><span style=\"font-family:微软雅黑;\">”必须添加，而且</span><span style=\"font-family:Arial;\">keyProperty</span><span style=\"font-family:微软雅黑;\">一定得和</span><span style=\"font-family:Arial;\">java</span><span style=\"font-family:微软雅黑;\">对象的属性名称一直，而不是表格的字段名</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:12.0pt;color:#366092;text-align:center;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;color:#333333;\">\n						<span style=\"font-family:Arial;\">2.java Dao</span><span style=\"font-family:微软雅黑;\">中的</span><span style=\"font-family:Arial;\">Insert</span><span style=\"font-family:微软雅黑;\">方法，传递的参数必须为</span><span style=\"font-family:Arial;\">java</span><span style=\"font-family:微软雅黑;\">对象，也就是</span><span style=\"font-family:Arial;\">Bean</span><span style=\"font-family:微软雅黑;\">，而不能是某个参数。</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', 'be4925e8', '0', null, 'admin001', '2019-02-14 12:56:46', null, null, '1', '97', '1');
INSERT INTO `hn_note` VALUES ('3ec6fe57', 'JS 弹窗代码', '<h3>\n	一、带返回状态的提示框\n</h3>\n<div>\n	<hr />\n</div>\n<p>\n	<br />\n</p>\n<pre class=\"prettyprint lang-js\">var is = confirm(\'确定XXX？\');\nif (is) {}\n</pre>\n<h3>\n	二、普通弹窗\n</h3>\n<div>\n	<hr />\n</div>\n<p>\n	<br />\n</p>\n<pre class=\"prettyprint lang-js\">\n<pre class=\"prettyprint lang-js\">alert(\"XXX?\"); \n</pre>\n</pre>\n<h3>\n	三、控制台打印数据\n</h3>\n<div>\n	<hr />\n</div>\n<p>\n	<br />\n</p>\n<pre class=\"prettyprint lang-js\">console.log(\"XXX\");</pre>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', '', '2', 'fdc9cfde', '0', null, 'admin001', '2019-02-19 18:02:05', null, null, '1', '1', '1');
INSERT INTO `hn_note` VALUES ('3fe73550', 'JS定时器，实现页面计时功能', '<p>\n	<strong>5.1 周期性定时器</strong> \n</p>\n<pre>1）setInterval(function函数，时间间隔ms);\n    启动周期性定时器，即让浏览器每隔1000毫秒调用一次匿名函数\n2）反回类型：定时器ID，用于停止定时器\n3）clearInterval(id); \n    停止定时器</pre>\n<p>\n	<strong>5.2一次性定时器</strong> \n</p>\n<pre>1）setTimeout(function函数，多长时间后执行ms); \n    启动一次性定时器，即在5000毫秒后调用匿名函数，调用后定时器自动停止\n2）反回类型：定时器ID，用于停止定时器\n3）clearInterval(id); \n    停止定时器</pre>\n<p>\n	<strong>5.2定时器实现计时功能</strong> \n</p>\n<pre>&lt;!DOCTYPE html&gt;\n&lt;html&gt;\n  &lt;head&gt;\n    &lt;title&gt;demo2.html&lt;/title&gt;\n\n    &lt;meta charset=\"UTF-8\"&gt;\n\n    &lt;!--&lt;link rel=\"stylesheet\" type=\"text/css\" href=\"./styles.css\"&gt;--&gt;\n      &lt;style&gt;\n            div{\n                  width: 400px;\n                  height: 160px;\n                  border:4px solid #ccc;\n                  text-align: center;\n                  margin: 50px auto;\n                  padding: 20px;\n            }\n            input{\n                  width: 150px;\n                  height: 50px;\n            }\n            .date{\n                  width: 300px;\n                  height: 50px;\n                  border:2px solid #ccc;\n                  line-height: 50px;\n                  font-size: 20px;\n                  margin: 0 auto;\n            }\n            .start{\n                  width: 300px;\n                  height: 50px;\n                  border:2px solid #ccc;\n                  line-height: 50px;\n                  font-size: 20px;\n                  margin: 0 auto;\n                  color: #000;\n            }\n            .stop{\n                  width: 300px;\n                  height: 50px;\n                  border:2px solid #ccc;\n                  line-height: 50px;\n                  font-size: 20px;\n                  margin: 0 auto;\n                  color: red;\n            }\n      &lt;/style&gt;\n      &lt;script&gt;\n            var id;\n            function start(){\n                  // 判断定时器是否已经启动\n                  if(id){\n                        return;\n                  }\n                  var p = document.getElementById(\"clock\");\n                  p.className=\"start\";\n                  // 启动定时器，每秒刷新一次人时间\n                  id = setInterval(function(){\n                        // 获取当前时间\n                        var d = new Date();\n                        // 格式化时间\n                        var time = d.toLocaleTimeString();\n                        // 显示时间\n                        p.innerHTML=time;\n                  },1000);\n            }\n\n            function stop(){\n                  var p = document.getElementById(\"clock\");\n                  p.className=\"stop\";\n                  clearInterval(id);\n                  id = undefined;\n            }\n      &lt;/script&gt;\n  &lt;/head&gt;\n\n  &lt;body&gt;\n      &lt;div&gt;\n            &lt;p&gt;\n                  &lt;input type=\"button\" value=\"开始\" onclick=\"start();\"&gt;\n                  &lt;input type=\"button\" value=\"停止\" onclick=\"stop();\"&gt;\n            &lt;/p&gt;\n            &lt;p class=\"date\" id=\"clock\"&gt;&lt;/p&gt;\n      &lt;/div&gt;\n  &lt;/body&gt;\n&lt;/html&gt;</pre>', '', '2', 'fdc9cfde', '0', null, 'admin001', '2019-02-19 18:04:37', null, null, '1', '1', '1');
INSERT INTO `hn_note` VALUES ('40b9cd95', 'rm 删除命令', '<p style=\"font-family:微软雅黑;font-size:20.0pt;\">\n	<span style=\"font-weight:bold;\">Linux 删除文件夹和文件的命令 </span> \n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"646\" height=\"178\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						-r\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						就是向下递归，不管有多少级目录，一并删除\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						-f\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						就是直接强行删除，不作任何提示的意思\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						删除文件夹实例：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						rm -rf\n  /var/log/httpd/access\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						将会删除/var/log/httpd/access目录以及其下所有文件、文件夹\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						删除文件使用实例：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						rm -f\n  /var/log/httpd/access.log\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:&quot;font-size:14.0pt;\">\n						将会强制删除/var/log/httpd/access.log这个文件\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '49c8fd8e', '0', null, 'admin001', '2019-02-19 18:18:17', null, null, '1', '98', '1');
INSERT INTO `hn_note` VALUES ('40fcd799', 'Redis 配置文件', '<p>\n	<span style=\"font-family:微软雅黑;font-size:10.5pt;\">在</span><span style=\"font-family:Calibri;font-size:10.5pt;\">Redis</span><span style=\"font-family:微软雅黑;font-size:10.5pt;\">目录下，打开</span><span style=\"font-weight:bold;font-family:Calibri;font-size:14.0pt;color:#FF0066;\"> redis.windows.conf</span><span style=\"font-family:Calibri;font-size:10.5pt;\"> </span><span style=\"font-family:微软雅黑;font-size:10.5pt;\">配置文件</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						配置项\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						值\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						说明\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						备注\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						bind\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:10.5pt;\">\n						ip\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						需要注释掉。因为这个是设置redis服务器只在本地监听，从而会拒绝来自外网的监听\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;\">如果出现Connection reset by peer: socket write\n  error异常，需要注释掉本项，并且设置以下配置项为</span><span style=\"font-family:Calibri;\">no</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						protected-mode\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:10.5pt;\">\n						yes /\n  no\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						是否以保护模式运行\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						requirepass\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:10.5pt;\">\n						password\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						登录密码\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						设置密码后，使用客户端需要输入密码\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						redis-cli -h\n  127.0.0.1 -p 6379 -a Passw0rd\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '98599daf', '0', null, 'admin001', '2019-02-19 18:16:44', null, null, '1', '98', '1');
