INSERT INTO `hn_note` VALUES ('460aceea', '正则表达式', '<div>\n	<table style=\"border-collapse:collapse;\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">一、校验数字的表达式</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[0-9]*$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						n位的数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\d{n}$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						至少n位的数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\d{n,}$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						m-n位的数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\d{m,n}$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						零和非零开头的数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^(0|[1-9][0-9]*)$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非零开头的最多带两位小数的数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^([1-9][0-9]*)+(.[0-9]{1,2})?$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						带1-2位小数的正数或负数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^(\\-)?\\d+(\\.\\d{1,2})?$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						正数、负数、和小数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^(\\-|\\+)?\\d+(\\.\\d+)?$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						有两位小数的正实数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[0-9]+(.[0-9]{2})?$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						有1~3位小数的正实数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[0-9]+(.[0-9]{1,3})?$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非零的正整数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[1-9]\\d*$ 或\n  ^([1-9][0-9]*){1,3}$ 或 ^\\+?[1-9][0-9]*$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非零的负整数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\-[1-9][]0-9″*$ 或\n  ^-[1-9]\\d*$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非负整数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\d+$ 或 ^[1-9]\\d*|0$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非正整数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^-[1-9]\\d*|0$ 或\n  ^((-\\d+)|(0+))$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非负浮点数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^\\d+(\\.\\d+)?$ 或\n  ^[1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*|0?\\.0+|0$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						非正浮点数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^((-\\d+(\\.\\d+)?)|(0+(\\.0+)?))$\n  或 ^(-([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*))|0?\\.0+|0$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						正浮点数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*$\n  或\n  ^(([0-9]+\\.[0-9]*[1-9][0-9]*)|([0-9]*[1-9][0-9]*\\.[0-9]+)|([0-9]*[1-9][0-9]*))$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						负浮点数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^-([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*)$\n  或\n  ^(-(([0-9]+\\.[0-9]*[1-9][0-9]*)|([0-9]*[1-9][0-9]*\\.[0-9]+)|([0-9]*[1-9][0-9]*)))$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						浮点数：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^(-?\\d+)(\\.\\d+)?$ 或\n  ^-?([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*|0?\\.0+|0)$\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"968\" height=\"449\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">二、校验字符的表达式</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[\\s\\S]+$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						所有格式字符串：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						汉字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[\\u4e00-\\u9fa5]{0,}$\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						英文和数字：\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						^[A-Za-z0-9]+$ 或\n  ^[A-Za-z0-9]{4,40}$\n	