INSERT INTO `hn_note` VALUES ('4d2274aa', 'MyBatis 配置（非集成）', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"915\" height=\"204\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">导包</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;dependency&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;groupId&gt;</span><span style=\"font-weight:bold;\">org.mybatis</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/groupId&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;artifactId&gt;</span><span style=\"font-weight:bold;\">mybatis</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/artifactId&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;version&gt;</span><span style=\"font-weight:bold;\">3.2.8</span><span style=\"font-weight:bold;color:#FF0066;\">&lt;/version&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/dependency&gt;</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>其他包：</span><span>ojdbc / junit</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"914\" height=\"199\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">添加</span><span style=\"font-weight:bold;\"> mybatis-config.xml </span><span style=\"font-weight:bold;\">配置文件</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;?xml version=\"1.0\"\n  encoding=\"UTF-8\" ?&gt;<span>&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;!DOCTYPE configuration PUBLIC\n  \"-//ibatis.apache.org//DTD Config 3.0//EN\" </span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">\"</span><a href=\"http://ibatis.apache.org/dtd/ibatis-3-config.dtd\"><span style=\"font-weight:bold;\">http://ibatis.apache.org/dtd/ibatis-3-config.dtd</span></a><span style=\"font-weight:bold;\">\"&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;configuration&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;\">&lt;!--</span><span style=\"font-weight:bold;font-family:Calibri;\"> </span><span style=\"font-weight:bold;font-family:微软雅黑;\">配置信息</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> --&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/configuration&gt; </span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"914\" height=\"328\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">3</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">在配置文件中配置数据库连接信息（此处使用</span><span style=\"font-weight:bold;\">Oracle</span><span style=\"font-weight:bold;\">数据库）</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;environments default=\"environment\"&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;environment\n  id=\"environment\"&gt;</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;transactionManager type=\"</span><span style=\"font-weight:bold;\">JDBC</span><span style=\"font-weight:bold;color:#FF0066;\">\"\n  /&gt;</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;dataSource\n  type=\"POOLED\"&gt;</span> \n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;\">driver</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;\">oracle.jdbc.OracleDriver</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;\">url</span><span style=\"font-weight:bold;color:#FF0066;\">\"value=\"</span><span style=\"font-weight:bold;\">jdbc:oracle:thin:@localhost:1521:xe</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;\">username</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;\">jsd1703</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;\">password</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;\">jsd1703</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/dataSource&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/environment&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/environments&gt;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"914\" height=\"146\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">4</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">在配置文件中指定映射文件</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\">&lt;!-- 指定映射文件的位置 --&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;mappers&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;mapper resource=\"</span><span style=\"font-weight:bold;color:black;\">entity/EmpMapper.xml</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/mappers&gt;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"911\" height=\"1683\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">5</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">创建实体类</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;\">创建实体类，添加属性，并为这些属性创建</span><span style=\"font-family:Calibri;\"> get/set </span><span style=\"font-family:微软雅黑;\">方法，</span><span style=\"font-family:Calibri;\">toString( ) </span><span style=\"font-family:微软雅黑;\">方法</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">注：属性名与表的字段名要一样（忽略大小写）。</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">6</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">创建实体类映射文件</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;\">创建实体类对应的</span><span style=\"font-family:Calibri;\"> mapper.mxl </span><span style=\"font-family:微软雅黑;\">映射文件</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">注：主要是一些sql语句。</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>如果插入</span><span style=\"font-weight:bold;color:#FF0066;\">null</span><span>，则需要在字段上添加：</span><span style=\"font-weight:bold;\">jdbcType=VARCHAR</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">如：#{age,jdbcType=VARCHAR}</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						不区分字段类型\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;?xml version=\"1.0\"\n  encoding=\"UTF-8\" ?&gt;<span>&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;!DOCTYPE mapper PUBLIC\n  \"-//ibatis.apache.org//DTD Mapper 3.0//EN\"<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>\"</span><a href=\"http://ibatis.apache.org/dtd/ibatis-3-mapper.dtd\"><span style=\"font-weight:bold;\">http://ibatis.apache.org/dtd/ibatis-3-mapper.dtd</span></a><span style=\"font-weight:bold;\">\"&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						&nbsp;\n		