INSERT INTO `hn_note` VALUES ('6860a7fe', 'ps 端口查看', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"795\" height=\"99\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;\">查看进程，获取</span><span style=\"font-family:Calibri;\">sid</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:Calibri;\">&nbsp;ps -ef | grep </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">服务名（如</span><span style=\"font-weight:bold;font-family:Calibri;color:#FF0066;\">tomcat</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">）</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:微软雅黑;\">通过进程</span><span style=\"font-family:Calibri;\">id</span><span style=\"font-family:微软雅黑;\">查看端口信息</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-family:Calibri;\">&nbsp;netstat -naop | grep </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">进程</span><span style=\"font-weight:bold;font-family:Calibri;color:#FF0066;\">id</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;查看某端口\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;netstat -ntpl | grep\n  22\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>', '', '2', '49c8fd8e', '0', null, 'admin001', '2019-02-19 18:18:34', null, null, '1', '97', '1');
