\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#A6A6A6;\">\n						<span style=\"font-weight:bold;\">&lt;!-- </span><span style=\"font-weight:bold;\">配置视图解释器</span><span style=\"font-weight:bold;\"> --&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;bean class=\"</span><span style=\"font-weight:bold;background:yellow;\">org.springframework.web.servlet.view.InternalResourceViewResolver</span><span style=\"font-weight:bold;color:#FF0066;\">\"&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#A6A6A6;\">\n						<span style=\"font-weight:bold;\">&lt;!-- prefix</span><span style=\"font-weight:bold;\">：前缀</span><span style=\"font-weight:bold;\"> --&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property\n  name=\"</span><span style=\"font-weight:bold;\">prefix</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;\">/WEB-INF/</span><span style=\"font-weight:bold;color:#FF0066;\">\"/&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#A6A6A6;\">\n						<span style=\"font-weight:bold;\">&lt;!-- suffix</span><span style=\"font-weight:bold;\">：后缀</span><span style=\"font-weight:bold;\"> --&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property\n  name=\"</span><span style=\"font-weight:bold;\">suffix</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;\">.html</span><span style=\"font-weight:bold;color:#FF0066;\">\"/&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/bean&gt;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;width:844px;\" class=\"ke-zeroborder\" height=\"589\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">5</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;\">创建</span><span style=\"font-weight:bold;font-family:Calibri;\">Controller</span><span style=\"font-weight:bold;font-family:微软雅黑;\">类，实现</span><span style=\"font-weight:bold;font-family:Calibri;\"> Controller </span><span style=\"font-weight:bold;font-family:微软雅黑;\">接口，并实现</span><span style=\"font-weight:bold;font-family:Calibri;\"> </span><span style=\"font-weight:bold;font-family:Calibri;\">handleRequest</span><span style=\"font-weight:bold;font-family:微软雅黑;\">（）方法</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">package com.horgn.shop.controller;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import javax.servlet.http.HttpServletRequest;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import javax.servlet.http.HttpServletResponse;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import org.springframework.web.servlet.ModelAndView;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import\n  org.springframework.web.servlet.mvc.Controller;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">public class MainController implements Controller{</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">public ModelAndView\n  handleRequest(HttpServletRequest arg0, HttpServletResponse arg1) throws\n  Exception {</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\">/*</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* ModelAndView有两个常用构造器：</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ModelAndView(String viewName)</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ModelAndView(String\n  viewName,Map data)</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* 注：</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;viewName：视图名</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;data：数据</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#A5A5A5;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*/</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">return new ModelAndView(\"</span><span style=\"font-weight:bold;\">pages/index</span><span style=\"font-weight:bold;color:#FF0066;\">\");</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">}</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">}</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;width:846px;\" class=\"ke-zeroborder\" height=\"68\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">6</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">运行项目</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '834bc892', '1', null, 'admin001', '2019-02-14 12:42:36', 'admin', '2019-02-19 17:55:50', '1', '90', '2');
