INSERT INTO `hn_note` VALUES ('89402cea', 'Eureka 服务注册中心', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"892\" height=\"36\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;\">\n						<span style=\"font-weight:bold;color:#366092;\">创建</span><span style=\"font-weight:bold;color:#366092;\">Mavena</span><span style=\"font-weight:bold;color:#366092;\">项目</span><span style=\"font-weight:bold;color:#FF0066;background:yellow;\">《服务注册》</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"894\" height=\"614\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">pom.xml 依赖配置</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependencies&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;<span style=\"font-weight:bold;\">spring-boot-starter-web</span>&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;1.4.0.RELEASE&lt;/version&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.springframework.cloud&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;<span style=\"font-weight:bold;color:#FF0066;background:yellow;\">spring-cloud-starter-eureka-server</span>&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependencies&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependencyManagement&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependencies&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.springframework.cloud&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;spring-cloud-dependencies&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;Brixton.RELEASE&lt;/version&gt;\n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;type&gt;pom&lt;/type&gt;\n					</p>\n					<p style=\"margin-left:1.125in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;scope&gt;import&lt;/scope&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependencies&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependencyManagement&gt;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"900\" height=\"361\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">3</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">创建 Application 启动类</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.springframework.boot.SpringApplication;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.springframework.boot.autoconfigure.SpringBootApplication;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.springframework.cloud.netflix.eureka.server.EnableEurekaServer;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						@EnableEurekaServer\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						@SpringBootApplication\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						public class\n  Application {\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						public\n  static void main(String[] args) {\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						SpringApplication.run(Application.class,\n  args);\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						}\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						}\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;background:yellow;\">@EnableEurekaServer</span><span style=\"font-family:Calibri;\"> </span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"903\" height=\"198\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">4</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">添加 application.properties 配置文件</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						server.port=1111\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						eureka.instance.hostname=localhost\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						eureka.client.register-with-eureka=false\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						eureka.client.fetch-registry=false\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						eureka.client.serviceUrl.defaultZone=http://localhost:${server.port}/eureka/\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">5</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">启动程序，在浏览器输入地址即可打开注册中心</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n						<a href=\"http://localhost:1111/\"><span style=\"font-weight:bold;background:yellow;\">http://localhost:1111/</span></a> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						可以看到，此时并没有服务，需要添加服务，请看下一节\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p>\n						<br />\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;<img src=\"data:image/png;base64,\" alt=\"\" width=\"889\" height=\"425\" /> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '41de6b8a', '0', null, 'admin001', '2019-02-19 18:12:55', null, null, '1', '99', '1');
INSERT INTO `hn_note` VALUES ('8a63c52c', 'Kingediter 初始化配置及图片上传接口', '<h3>\n	一、下载并引入kingediter样式及js\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-js\">&lt;script type=\"text/javascript\" src=\"${base}/jquery/jquery-1.11.1.js\"&gt;&lt;/script&gt;\n&lt;script type=\"text/javascript\" src=\"${base}/kindeditor/kindeditor-all.js\"&gt;&lt;/script&gt;\n&lt;script charset=\"utf-8\" src=\"${base}/kindeditor/lang/zh-CN.js\"&gt;&lt;/script&gt;</pre>\n<h3>\n	二、创建textarea标签并初始化编辑器\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-js\">&lt;textarea id=\"noteContant\" name=\"noteContant\" class=\"form-control\" rows=\"20\"&gt;&lt;/textarea&gt;\n\n// 初始化编辑器\nfunction initKindEditor(id) {\n   _editor = KindEditor.create(id, {\n      width: \'100%\',\n      height: \'5000px\',\n      resizeMode: 0,\n      resizeType: 1, //类型：1不可调节宽度，\n      allowPreviewEmoticons: false,\n      // formatUploadUrl:false,\n      afterBlur: function() {\n         this.sync();\n      }, //编辑器失去焦点(blur)时执行的回调函数（将编辑器的HTML数据同步到textarea）\n      uploadJson: \'/note/note/uploadImg.do\', //图片上传地址\n      items: [\n         \'source\', \'|\', \'undo\', \'redo\', \'|\', \'preview\', \'print\', \'template\', \'code\', \'cut\', \'copy\', \'paste\',\n         \'plainpaste\', \'wordpaste\', \'|\', \'justifyleft\', \'justifycenter\', \'justifyright\',\n         \'justifyfull\', \'insertorderedlist\', \'insertunorderedlist\', \'indent\', \'outdent\', \'subscript\',\n         \'superscript\', \'clearhtml\', \'quickformat\', \'selectall\', \'|\', \'fullscreen\', \'/\',\n         \'formatblock\', \'fontname\', \'fontsize\', \'|\', \'forecolor\', \'hilitecolor\', \'bold\',\n         \'italic\', \'underline\', \'strikethrough\', \'lineheight\', \'removeformat\', \'|\', \'image\', \'multiimage\',\n         \'flash\', \'media\', \'insertfile\', \'table\', \'hr\', \'emoticons\', \'baidumap\', \'pagebreak\',\n         \'anchor\', \'link\', \'unlink\', \'|\', \'about\'\n      ]\n   });\n}\n</pre>\n<h3>\n	三、保存文本时获取文本内容\n</h3>\n<div>\n	<hr />\n</div>\n<pre class=\"prettyprint lang-js\">   _editor.sync();\n   var html = _editor.html();   //获取内容\n   var html = _editor.html(\"设置内容\");</pre>\n<h3>\n	四、图片上传接口\n</h3>\n<div>\n	<hr />\n</div>\n<pre class=\"prettyprint lang-js\">@RequestMapping(\"/uploadImg\")\n@ResponseBody\npublic Map&lt;String, Object&gt; doUploadImg(HttpServletRequest request) {\n	Map&lt;String, Object&gt; result = new HashMap&lt;String, Object&gt;();\n	String url = null;\n	String fileName = UUID.randomUUID().toString().replaceAll(\"-\", \"\");\n	String pathName = \"images/document\";\n	String fileType = \".jpg\";\n	String fileN = \"imgFile\";\n	boolean ok = true;\n	try {\n		ok = FileUtil.uploadFile(request, fileName, pathName, fileType, fileN);\n	} catch (Exception e1) {\n		e1.printStackTrace();\n	}\n	if (ok) {\n		url = http + address + \":\" + port + appname + \"/\" + pathName + \"/\" + fileName + fileType;\n		result.put(\"error\", 0);\n		result.put(\"url\", url);\n	} else {\n		result.put(\"error\", 1);\n	}\n	return result;\n}</pre>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', '', '2', 'b2593480', '0', null, 'admin001', '2019-02-19 18:26:32', null, null, '1', '1', '1');
INSERT INTO `hn_note` VALUES ('8a9a24ac', 'SpringBoot 配置 Tomcat', '<p style=\"font-family:微软雅黑;font-size:16.0pt;color:#E36C09;\">\n	<span style=\"font-weight:bold;\">（方法一）</span><span style=\"font-weight:bold;\"> </span><span style=\"font-weight:bold;\">配置文件方式</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"1002\" height=\"36\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">在</span><span style=\"font-weight:bold;\"> application.properties </span><span style=\"font-weight:bold;\">或</span><span style=\"font-weight:bold;\"> application.yml </span><span style=\"font-weight:bold;\">配置文中进行配置</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">可配置内容如下：</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<div>\n						<table style=\"border-collapse:collapse;\" width=\"991\" height=\"1024\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n							<tbody>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.port=</span>8050\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											修改端口号\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.address=</span>127.0.0.1\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-size:10.5pt;\">\n											<span style=\"font-family:微软雅黑;\">修改</span><span style=\"font-family:Calibri;\">ip</span><span style=\"font-family:微软雅黑;\">地址</span> \n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.context-path=</span>/springboot\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											指定项目名称\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											在正常的项目中，项目路径都是localhost:8080+项目名称+相应的访问路径，在spring\n    boot中默认并没有项目名称而是直接访问localhost:8080/访问路径，这种方式的路径格式肯定是不好的，因此我们要为项目指定context-path\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.tomcat.uri-encoding=</span>UTF-8\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#333333;\">\n											指定tomcat的编码格式\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.session-timeout=</span> \n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											session最大超时时间(分钟)，默认为30\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.servlet-path=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.tomcat.access-log-pattern=</span> \n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											Access日志格式\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-size:10.5pt;\">\n											<span style=\"font-weight:bold;font-family:微软雅黑;\">server.tomcat.access-log-enabled=</span><span style=\"font-family:Calibri;\">true</span> \n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											打开Tomcat的Access日志\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.accesslog.directory=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span>&nbsp;</span>accesslog目录，默认basedir/logs\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.protocol-header=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.remote-ip-header=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.basedir=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											存放Tomcat的日志、Dump等文件的临时文件夹\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.background-processor-delay=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.max-threads=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.uri-encoding=UTF-8\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.tomcat.max-threads=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											tomcat最大线程数，默认为200\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											server.error.path=\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											错误页，指定发生错误时，跳转的URL。\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.ssl.enabled=</span>true\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											启动tomcat ssl配置\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-size:10.5pt;\">\n											<span style=\"font-family:微软雅黑;\">开启</span><span style=\"font-family:Calibri;\"> Https </span><span style=\"font-weight:bold;font-family:微软雅黑;\">（先配置端口为</span><span style=\"font-weight:bold;font-family:Calibri;\">8443</span><span style=\"font-weight:bold;font-family:微软雅黑;\">）</span> \n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.ssl.keyStorePassword=</span>123456\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											密码\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.ssl.keyStore=</span><span>classpath:</span><span>xxx.</span><span>pfx</span> \n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											证书位置\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">server.ssl.keyStoreType</span>=PKCS12\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											证书格式\n										</p>\n									</td>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											&nbsp;\n										</p>\n									</td>\n								</tr>\n							</tbody>\n						</table>\n					</div>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:16.0pt;color:#E36C09;\">\n	<span style=\"font-weight:bold;\">（方法二）配置类方式</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"1003\" height=\"36\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">创建配置类 TomcatConfig.java，使用\n  @Configuration 注解</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"1001\" height=\"590\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">添加 @Bean 注解及配置对象</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import\n  org.springframework.boot.context.embedded.EmbeddedServletContainerFactory;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import\n  org.springframework.boot.context.embedded.tomcat.TomcatEmbeddedServletContainerFactory;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import org.springframework.context.annotation.Bean;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">import\n  org.springframework.context.annotation.Configuration;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">/**</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* Tomcat 配置</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* @author\n  Horgn黄小锤</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* @version\n  v1.0</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>* @date\n  2018-01-24 15:28:27</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;</span>*/</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Configuration</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">public class TomcatConfig {</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Bean</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">public </span><span style=\"font-weight:bold;color:#FF0066;\">EmbeddedServletContainerFactory</span><span style=\"font-weight:bold;\"> servletContainer(){</span> \n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">TomcatEmbeddedServletContainerFactory</span><span style=\"font-weight:bold;\"> </span><span style=\"font-weight:bold;color:#0070C0;\">tomcat</span><span style=\"font-weight:bold;\"> = new </span><span style=\"font-weight:bold;color:#FF0066;\">TomcatEmbeddedServletContainerFactory</span><span style=\"font-weight:bold;\">();</span> \n					</p>\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;color:#7F7F7F;\">//tomcat.set...</span> \n					</p>\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">return </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">tomcat</span><span style=\"font-weight:bold;font-family:微软雅黑;\">;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">}</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">}</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"1001\" height=\"693\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">3</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">多端口监听配置</span><span style=\"font-weight:bold;\">（</span><span style=\"font-weight:bold;\">Http </span><span style=\"font-weight:bold;\">与</span><span style=\"font-weight:bold;\"> Https</span><span style=\"font-weight:bold;\">）</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						前面启动ssl后只能走https,不能通过http进行访问，如果要监听多端口，可采用编码形式实现。\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@Configuration</span><span style=\"font-weight:bold;\"><br />\npublic class TomcatConfig {</span> \n					</p>\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">@Bean</span><span style=\"font-weight:bold;font-family:微软雅黑;\"><br />\n<span>&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">public </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">EmbeddedServletContainerFactory</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> servletContainer()\n  {<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\"><span>&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">TomcatEmbeddedServletContainerFactory</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> tomcat = new </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">TomcatEmbeddedServletContainerFactory</span><span style=\"font-weight:bold;font-family:微软雅黑;\">();<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">tomcat.setUriEncoding(\"UTF-8\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">tomcat.</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">addAdditionalTomcatConnectors</span><span style=\"font-weight:bold;font-family:微软雅黑;\">(</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">createSslConnector</span><span style=\"font-weight:bold;font-family:微软雅黑;\">());<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">return </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">tomcat</span><span style=\"font-weight:bold;font-family:微软雅黑;\">;<br />\n<span>&nbsp;&nbsp;&nbsp; </span>}</span> \n					</p>\n					<p style=\"font-size:10.5pt;\">\n						<span style=\"font-weight:bold;font-family:微软雅黑;\">private </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">Connector</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> createSslConnector()\n  {<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">Connector</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> connector = new </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">Connector</span><span style=\"font-weight:bold;font-family:微软雅黑;\">(\"</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">org.apache.coyote.http11.Http11NioProtocol</span><span style=\"font-weight:bold;font-family:微软雅黑;\">\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">Http11NioProtocol</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> protocol =\n  (Http11NioProtocol) connector.</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#FF0066;\">getProtocolHandler</span><span style=\"font-weight:bold;font-family:微软雅黑;\">();<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>try {<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">File </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">truststore</span><span style=\"font-weight:bold;font-family:微软雅黑;\"> = new File(\"</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">/Users/liaokailin/software/ca1/keystore</span><span style=\"font-weight:bold;font-family:微软雅黑;\">\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">connector.setScheme(\"</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">https</span><span style=\"font-weight:bold;font-family:微软雅黑;\">\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">protocol.setSSLEnabled(</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">true</span><span style=\"font-weight:bold;font-family:微软雅黑;\">);<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">connector.setSecure(</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">true</span><span style=\"font-weight:bold;font-family:微软雅黑;\">);<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">connector.setPort(</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">8443</span><span style=\"font-weight:bold;font-family:微软雅黑;\">);<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">protocol.setKeystoreFile(</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">truststore.getAbsolutePath()</span><span style=\"font-weight:bold;font-family:微软雅黑;\">);<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">protocol.setKeystorePass(\"</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">123456</span><span style=\"font-weight:bold;font-family:微软雅黑;\">\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">protocol.setKeyAlias(\"</span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">springboot</span><span style=\"font-weight:bold;font-family:微软雅黑;\">\");<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">return connector;<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>} catch (Exception ex)\n  {<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;font-family:Calibri;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-weight:bold;font-family:微软雅黑;\">throw new </span><span style=\"font-weight:bold;font-family:微软雅黑;color:#0070C0;\">IllegalStateException</span><span style=\"font-weight:bold;font-family:微软雅黑;\">(\"cant access\n  keystore: [\" + \"keystore\" + \"]<span>&nbsp; </span>\", ex);<br />\n<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>}<br />\n<span>&nbsp;&nbsp;&nbsp; </span>}<br />\n}</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '5b7f1962', '0', null, 'admin001', '2019-02-19 18: