INSERT INTO `hn_note` VALUES ('9300c395', 'KindEditor 初始化参数', '<div class=\"documentwrapper\">\n	<div class=\"bodywrapper\">\n		<div class=\"body\">\n			<div class=\"section\" id=\"id1\">\n				<h1>\n					<a class=\"toc-backref\" href=\"http://kindeditor.net/docs/option.html#id60\">编辑器初始化参数</a><a class=\"headerlink\" href=\"http://kindeditor.net/docs/option.html#id1\">¶</a>\n				</h1>\n				<div class=\"contents topic\" id=\"contents\">\n					<p class=\"topic-title first\">\n						Contents\n					</p>\n					<ul class=\"simple\">\n						<li>\n							<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#id1\" id=\"id60\">编辑器初始化参数</a>\n							<ul>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#width\" id=\"id61\">width</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#height\" id=\"id62\">height</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#minwidth\" id=\"id63\">minWidth</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#minheight\" id=\"id64\">minHeight</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#items\" id=\"id65\">items</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#nodisableitems\" id=\"id66\">noDisableItems</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#filtermode\" id=\"id67\">filterMode</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.html#htmltags\" id=\"id68\">htmlTags</a>\n								</li>\n								<li>\n									<a class=\"reference internal\" href=\"http://kindeditor.net/docs/option.htm