INSERT INTO `hn_note` VALUES ('9f2652d0', 'Redis 测试连接 Jedis类', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"920\" height=\"415\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>需要</span><span>jar</span><span>包</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;\">\n						jedis-2.8.0.jar\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;\">\n						commons-pool2-2.4.2.jar\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependencies&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>&nbsp;</span>&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;redis.clients&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;jedis&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;2.9.0&lt;/version&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.apache.commons&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;commons-pool2&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;2.4.2&lt;/version&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependencies&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"919\" height=\"397\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						添加测试\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						public static void\n  main(String[] args) {\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>//</span><span>注：端口号</span><span>6379</span><span>是默认端口，如果为默认端口则以下接口</span><span style=\"font-weight:bold;\">可省略不写</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">Jedis jedis = new\n  Jedis(\"192.168.1.178\"，</span><span style=\"font-weight:bold;\">6379)</span><span style=\"font-weight:bold;\">;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						//设置密码\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">jedis.auth(\"hpg0412\");</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						System.out.println(\"连接成功!\");\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						//\n  查看服务是否运行\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						System.out.println(\"Server\n  is running: \" + <span style=\"font-weight:bold;\">jedis.ping()</span>);\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						String\n  keys = \"name\";\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						jedis.del(keys);\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						String\n  vaule = jedis.set(keys, \"tanggao\");\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						System.out.println(vaule);\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						String\n  name = jedis.get(\"name\");\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						System.out.println(name);\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						}\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"917\" height=\"292\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">接口</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">参数</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">作用</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>new </span><span style=\"font-weight:bold;color:#FF0066;\">Jedis</span><span>(</span><span>ip</span><span>，</span><span>port)</span><span>;</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>ip</span><span>地址，接口（可省略）</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						构造方法\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">auth</span>(password)\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						登录密码\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						在构造方法后，在其它操作前，要先配置密码\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">ping</span>()\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						查看运行状态\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">del</span>(key)\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						key\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>删除</span><span>key</span><span>对应的值</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">set</span>(key,value)\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						key,value\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						添加数据\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">get</span>(key)\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						key\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						获取数据\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>', '', '2', '98599daf', '0', null, 'admin001', '2019-02-19 18:17:02', null, null, '1', '97', '1');
INSERT INTO `hn_note` VALUES ('a0280736', '定时器（时间规则）', '<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	单纯针对时间的设置规则\n</p>\n<p style=\"font-size:10.5pt;\">\n	<span style=\"font-family:arial;\">org.springframework.scheduling.quartz.CronTriggerBean</span><span style=\"font-family:微软雅黑;\">允许你更精确地控制任务的运行时间，只需要设置其</span><span style=\"font-family:arial;\">cronExpression</span><span style=\"font-family:微软雅黑;\">属性。</span> \n</p>\n<p style=\"font-size:10.5pt;\">\n	<span style=\"font-family:微软雅黑;\">一个</span><span style=\"font-family:arial;\">cronExpression</span><span style=\"font-family:微软雅黑;\">表达式有至少</span><span style=\"font-family:arial;\">6</span><span style=\"font-family:微软雅黑;\">个（也可能是</span><span style=\"font-family:arial;\">7</span><span style=\"font-family:微软雅黑;\">个）由空格分隔的时间元素。从左至右，这些元素的定义如下：</span> \n</p>\n<p style=\"font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;font-family:arial;\">1</span><span style=\"font-weight:bold;font-family:微软雅黑;\">．秒（</span><span style=\"font-weight:bold;font-family:arial;\">0</span><span style=\"font-weight:bold;font-family:微软雅黑;\">–</span><span style=\"font-weight:bold;font-family:arial;\">59</span><span style=\"font-weight:bold;font-family:微软雅黑;\">）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">2．分钟（0–59）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">3．小时（0–23）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">4．月份中的日期（1–31）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">5．月份（1–12或JAN–DEC）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">6．星期中的日期（1–7或SUN–SAT）</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">7．年份（1970–2099</span>）\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	0 0 10,14,16 * * ?\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	每天上午10点,下午2点和下午4点\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	0 0,15,30,45 * 1-10 *\n  ?\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	每月前10天每隔15分钟\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	30 0 0 1 1 ? 2012\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	在2012年1月1日午夜过30秒时\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	0 0 8-5 ? * MON-FRI\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	每个工作日的工作时间\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">各个时间可用值如下：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">秒0-59 , - * /</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">分0-59 , - * /</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">小时0-23 , - * /</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">日1-31 , - * ? / L W C</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">月1-12 or JAN-DEC , - * /</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">周几1-7 or SUN-SAT , - * ? / L C #</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n	<span style=\"font-weight:bold;\">年(可选字段) empty, 1970-2099 , - * /</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	可用值详细分析如下：\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“*”</span>——字符可以用于所有字段，在“分”字段中设为\"*\"表示\"每一分钟\"的含义。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“?”</span>——字符可以用在“日”和“周几”字段.它用来指定\'不明确的值\'.这在你需要指定这两个字段中的某一个值而不是另外一个的时候会被用到。在后面的例子中可以看到其含义。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“-”</span>——字符被用来指定一个值的范围，比如在“小时”字段中设为\"10-12\"表示\"10点到12点\"。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“,”</span>——字符指定数个值。比如在“周几”字段中设为\"MON,WED,FRI\"表示\"the\n  days Monday, Wednesday, and Friday\"。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“/”</span>——字符用来指定一个值的的增加幅度.比如在“秒”字段中设置为\"0/15\"表示\"第0,\n  15, 30,和45秒\"。而\"5/15\"则表示\"第5, 20,\n  35,和50\".在\'/\'前加\"*\"字符相当于指定从0秒开始.每个字段都有一系列可以开始或结束的数值。对于“秒”和“分”字段来说，其数值范围为0到59，对于“小时”字段来说其为0到23,对于<span style=\"font-weight:bold;color:#FF0066;\">“日”</span>字段来说为0到31,而对于“月”字段来说为1到12。\"/\"字段仅仅只是帮助你在允许的数值范围内从开始\"第n\"的值。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“L”</span><span>——字符可用在“日”和“周几”这两个字段。它是\"last\"的缩写,但是在这两个字段中有不同的含义。例如,“日”字段中的\"L\"表示\"一个月中的最后一天\"\n  ——对于一月就是31号对于二月来说就是28号（非闰年）。而在“周几”字段中,它简单的表示\"7\" or\n  \"SAT\"，但是如果在“周几”字段中使用时跟在某个数字之后,它表示\"该月最后一个星期×</span><span>\" </span><span>——比如\"6L\"表示\"该月最后一个周五\"。当使用\'L\'选项时,指定确定的列表或者范围非常重要，否则你会被结果搞糊涂的。</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“W</span>”——可用于“日”字段。用来指定历给定日期最近的工作日(周一到周五)。比如你将“日”字段设为\"15W\"，意为:\n\"离该月15号最近的工作日\"。因此如果15号为周六，触发器会在14号即周五调用。如果15号为周日,触发器会在16号也就是周一触发。如果15号为周二,那么当天就会触发。然而如果你将“日”字段设为\"1W\",而一号又是周六,触发器会于下周一也就是当月的3号触发,因为它不会越过当月的值的范围边界。\'W\'字符只能用于“日”字段的值为单独的一天而不是一系列值的时候。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“L”</span>和“W”可以组合用于“日”字段表示为\'LW\'，意为\"该月最后一个工作日\"。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“#”</span><span>——字符可用于“周几”字段。该字符表示“该月第几个周×”，比如</span><span>\"6#3\"</span><span>表示该月第三个周五</span><span>( 6</span><span>表示周五而</span><span>\"#3\"</span><span>该月第三个</span><span>)</span><span>。再比如</span><span>: \"2#1\" =</span><span>表示该月第一个周一而</span><span>\"4#5\" =</span><span>该月第五个周三。注意如果你指定</span><span>\"#5\"</span><span>该月没有第五个“周×”，该月是不会触发的。</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;color:#FF0066;\">“C”</span>——字符可用于“日”和“周几”字段，它是\"calendar\"的缩写。它表示为基于相关的日历所计算出的值（如果有的话）。如果没有关联的日历,那它等同于包含全部日历。“日”字段值为\"5C\"表示\"日历中的第一天或者5号以后\"，“周几”字段值为\"1C\"则表示\"日历中的第一天或者周日以后\"。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	对于“月份”字段和“周几”字段来说合法的字符都不是大小写敏感的。\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span style=\"font-weight:bold;\">一些例子：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 0 12 * *\n  ?\"每天中午十二点触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 ? *\n  *\"每天早上10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 * *\n  ?\"每天早上10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 * * ?\n  *\"每天早上10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 * * ?\n  2005\" 2005年的每天早上10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 * 14 * *\n  ?\"每天从下午2点开始到2点59分每分钟一次触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 0/5 14 * *\n  ?\"每天从下午2点开始到2：55分结束每5分钟一次触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 0/5 14,18 * *\n  ?\"每天的下午2点至2：55和6点至6点55分两个时间段内每5分钟一次触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 0-5 14 * *\n  ?\"每天14:00至14:05每分钟一次触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 10,44 14 ? 3\n  WED\"三月的每周三的14：10和14：44触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 ? *\n  MON-FRI\"每个周一、周二、周三、周四、周五的10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 15 *\n  ?\"每月15号的10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 L *\n  ?\"每月的最后一天的10：15触发\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	\"0 15 10 ? *\n  6L\"每月最后一个周五的10：15\n</p>', '', '2', '86cd00d8', '9', null, 'admin001', '2019-02-14 12:39:34', null, null, '1', '1', '1');
INSERT INTO `hn_note` VALUES ('a5b59cb9', 'Oracle 查询语句、综合信息', '<div>\n	<h3>\n		命令登录Oracle数据库\n		<hr />\n	</h3>\n<pre class=\"prettyprint lang-js\">1、运行SQLPLUS工具\n\n&emsp;&emsp;C:\\Users\\wd-pc&gt;sqlplus\n\n2、直接进入SQLPLUS命令提示符\n\n&emsp;&emsp;C:\\Users\\wd-pc&gt;sqlplus /nolog\n\n3、以OS身份连接 \n\n&emsp;&emsp;C:\\Users\\wd-pc&gt;sqlplus / as sysdba   或\n\n&emsp;&emsp;SQL&gt;connect / as sysdba\n\n4、普通用户登录\n\n&emsp;&emsp;C:\\Users\\wd-pc&gt;sqlplus scott/123456 &emsp;或\n\n&emsp;&emsp;SQL&gt;connect scott/123456  或\n\n&emsp;&emsp;SQL&gt;connect scott/123456@servername\n\n5、以管理员登录\n\n&emsp;&emsp;C:\\Users\\wd-pc&gt;sqlplus sys/123456 as sysdba&emsp;或\n\n&emsp;&emsp;SQL&gt;connect sys/123456 as sysdba\n\n6、切换用户\n\n&emsp;&emsp;SQL&gt;conn hr/123456 \n\n&emsp;&emsp;注：conn同connect\n\n 7、退出\n\n&emsp;&emsp;exit</pre>\n	<h3>\n		Oracle g11关闭密码大小写敏感\n		<hr />\n	</h3>\n<pre class=\"prettyprint lang-js\">Oracle 从11g开始默认大小写敏感。\n\n查看是否大小写敏感：\nSHOW PARAMETER SEC_CASE;\n设置大小写不敏感：\nALTER SYSTEM SET SEC_CASE_SENSITIVE_LOGON = FALSE;\n \n注：以上修改方法对sys密码无效</pre>\n</div>\n<h3>\n	日期格式化查询\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-js\">select * from 表名 where to_char(字段名,\'yyyy-mm-dd\')=\'2014-01-01\'</pre>\n<h3>\n	大文本保存\n	<hr />\n</h3>\n<p>\n	数据库类型：BLOB\n</p>\n<p>\n	java类型：\n</p>\n<pre class=\"prettyprint lang-js\">//	@Column(name=\"NOTE_CONTANT\",nullable=true)\n//	@ColumnType(jdbcType=JdbcType.BLOB)\nprivate byte[] noteContant;</pre>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', '', '2', 'f36b17a6', '0', null, 'admin001', '2019-02-14 13:01:54', null, null, '1', '98', '1');
INSERT INTO `hn_note` VALUES ('a639f33a', 'MyBatis 查询语句', '<p>\n	<br />\n</p>\n<h2>\n	大于号小于号的写法\n	<hr />\n</h2>\n<pre class=\"prettyprint lang-js\">第一种写法（1）： 原符号 &lt;     &lt;=      &gt;      &gt;=        &amp;        \'        \" \n替换符号                &amp;lt;   &amp;lt;=   &amp;gt;   &amp;gt;=     &amp;amp;    &amp;apos;    &amp;quot; \n例如：sql如下： create_date_time &amp;gt;= #{startTime} and create_date_time &amp;lt;= #{endTime} \n\n第二种写法（2）： 大于等于 &lt;![CDATA[ &gt;= ]]&gt; 小于等于 &lt;![CDATA[ &lt;= ]]&gt; \n例如：sql如下： create_date_time &lt;![CDATA[ &gt;= ]]&gt; #{startTime} and create_date_time &lt;![CDATA[ &lt;= ]]&gt; #{endTime}\n</pre>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>', '', '2', 'be4925e8', '0', null, 'admin001', '2019-02-14 12:59:36', null, null, '1', '93', '1');
INSERT INTO `hn_note` VALUES ('a8c016fc', 'SpringBoot 单元测试', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"803\" height=\"432\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">导包</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;&nbsp;&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.springframework.boot&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;spring-boot-starter-test&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;scope&gt;test&lt;/scope&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;org.springframework&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;spring-test&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;4.3.6.RELEASE&lt;/version&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;dependency&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;groupId&gt;junit&lt;/groupId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;artifactId&gt;junit&lt;/artifactId&gt;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;version&gt;\n  4.12&lt;/version&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&lt;/dependency&gt;\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"804\" height=\"796\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:Calibri;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2 <br />\n</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">创建测试类</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  java.util.UUID;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  javax.annotation.Resource;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.junit.Test;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.junit.runner.RunWith;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.springframework.boot.test.context.SpringBootTest;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  org.springframework.test.context.junit4.SpringJUnit4ClassRunner;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  com.frame.system.tuser.entity.TUser;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						import\n  com.frame.system.tuser.service.TUserService;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@RunWith(</span><span style=\"font-weight:bold;color:#1F497D;\">SpringJUnit4ClassRunner</span><span style=\"font-weight:bold;color:#FF0066;\">.class)</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@SpringBootTest(webEnvironment = </span><span style=\"font-weight:bold;color:#1F497D;\">SpringBootTest.WebEnvironment.RANDOM_PORT</span><span style=\"font-weight:bold;color:#FF0066;\">)</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						public class\n  TUserServiceTest {\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">@Resource</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						private\n  TUserService service;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">@Test</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						public\n  void test(){\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						TUser\n  entity = new TUser();\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						entity.setId(UUID.randomUUID().toString());\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						entity.setUsername(\"hpg\");\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						entity.setPassword(\"1111\");\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						service.save(entity);\n					</p>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						System.out.println(\"ok\");\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						}\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						}\n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', '5b7f1962', '0', null, 'admin001', '2019-02-19 18:11:58', null, null, '1', '88', '1');
INSERT INTO `hn_note` VALUES ('abe44076', '集合操作', '<h3>\n	一、自定义比较器 Comparator\n	<hr />\n</h3>\n<p>\n	实体：\n</p>\n<pre class=\"prettyprint lang-js\">class Cat{\n    int age;\n    public Cat(int age){\n        this.age = age;\n    }\n    public String toString(){\n        return \"[\"+age+\"]\";\n    }\n}</pre>\n<p>\n	比较器：\n</p>\n<pre class=\"prettyprint lang-js\">class ByAge implements Comparator&lt;Cat&gt;{\n    /*\n     * 自定义的比较算法:按猫的年龄大小比较\n     * \n     * 如果返回&gt;0:o1&gt;o2\n     * 如果返回&lt;0:o1&lt;o2\n     * 如果返回=0:o1=o2\n     */\n    public int compare(Cat o1,Cat o2){\n        int a1 = o1.age;\n        int a2 = o2.age;\n\n        return a1 - a2;\n    }\n}\n</pre>\n<p>\n	比较List集合（排序）：\n</p>\n<p>\n	<br />\n</p>\n<pre class=\"prettyprint lang-js\">List&lt;Cat&gt; list = new ArrayList&lt;Cat&gt;();\nCollections.sort(list,new ByAge());</pre>\n<h3>\n	二、Iterator 迭代器(用于遍历集合)\n	<hr />\n</h3>\n<p>\n	注：需要与while循环搭配 <br />\n注：for循环遍历期间不能删除（remove）集合中的元素，想删除元素，则换成传统的写法（iterator）\n</p>\n<pre class=\"prettyprint lang-js\">Collection c1 = new ArrayList();\nc1.add(\"Tom\");\nc1.add(\"Andy\");\nc1.add(\"熊大\");\nc1.add(\"熊二\");\nc1.add(\"dog鹏\");\n/*\n * 遍历集合中的每个元素，iterator专门与while搭配\n * hasNext()检查ite中游标的下个位置是否有引用\n * \n * next()是将游标移动一次，并返回引用（元素的值）\n * 如果移动越界将抛出异常\n * 返回值引用集合中的元素\n */\nIterator ite = c1.iterator();\nwhile(ite.hasNext()){\n    Object obj = ite.next();\n    System.out.println(obj);\n    /*\n     * 删除值为“熊二”的元素\n     * 此处为迭代后才进行删除，所以会输出一个元素\n     * 输出完后剩余4个元素\n     */\n    if(\"熊二\".equals(obj)){   //obj写在后面，避免空指针异常\n        ite.remove();\n}</pre>\n<h3>\n	三、遍历Map集合---Entry方法\n	<hr />\n</h3>\n<pre class=\"prettyprint lang-js\">for(Entry&lt;Integer,String&gt; en : map.entrySet()){\n    en.getValue();\n    en.getKey();\n}</pre>\n<p>\n	<br />\n</p>\n<br />\n<p>\n	<br />\n</p>', '', '2', '03afefc7', '95', null, 'admin001', '2019-02-14 12:41:05', null, null, '1', '1', '1');
