INSERT INTO `hn_note` VALUES ('f0039d04', '55', '<div>\n	<div style=\"margin-left:0in;\">\n		<div style=\"margin-left:0in;\">\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				<span>1</span><span>、视图</span>\n			</p>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				&nbsp;\n			</p>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				人员工作量视图\n			</p>\n			<div>\n				<table style=\"border-collapse:collapse;\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n					<tbody>\n						<tr>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp; </span>\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									CREATE\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp;&nbsp;&nbsp; </span>/*[ALGORITHM = {UNDEFINED | MERGE |\n  TEMPTABLE}]\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp;&nbsp;&nbsp; </span>[DEFINER = { user | CURRENT_USER }]\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp;&nbsp;&nbsp; </span>[SQL SECURITY { DEFINER | INVOKER }]*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp;&nbsp;&nbsp; </span>VIEW\n  `HospitalReport`.`view_stat_workinfo`\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp;&nbsp;&nbsp; </span>AS\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									(\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.sId,/**/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.RoomName,/*机房类型（部门）*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.MachineryRoomName,/*机房名称*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.ProjectName,/*检查项目*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.PatientSourceText,/*患者来源*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.ApplyPersonName,/*申请人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.DepartName,/*申请科室*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									a.ChargeStateText,/*收费状态*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									i.sNurseName AS\n  NurseName,/*注射人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									q.sDoctorName AS\n  ConsultingDoctor,/*问诊人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									r.sReportDoctorName\n  AS Dictator,/*报告 医学*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									r.sExamineName AS\n  ExamineName<span>&nbsp; </span>/*审核人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									FROM\n  view_stat_patientbaseinfo a /*基本表*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN\n  InjectionInfo i ON a.PatientId = i.sPatientId /*注射表*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN Inquisition\n  q ON a.PatientId = q.sPatientId /*问诊表*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN Report r ON\n  a.PatientId = r.sPatientId /*报告表*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN\n  OperateComputer o ON a.PatientId = o.sPatientId /*上机表**/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN FollowUp f\n  ON a.PatientId = f.sPatientId /*随访表*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									LEFT JOIN Register g\n  ON a.PatientId = g.sPatienId /*预约表*/\n								</p>\n								<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									);\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n						</tr>\n						<tr>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n						</tr>\n						<tr>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									<span>&nbsp; </span>\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*注射人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sNurseName\n  AS UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a\n  LEFT JOIN InjectionInfo r ON a.PatientId = r.sPatientId GROUP BY\n  r.sNurseName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*上机人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sOperator AS\n  UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a LEFT\n  JOIN OperateComputer r ON a.PatientId = r.sPatientId GROUP BY\n  r.sOperator,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*随访人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sDoctorName\n  AS UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a\n  LEFT JOIN FollowUp r ON a.PatientId = r.sPatientId GROUP BY\n  r.sDoctorName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*预约人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT\n  r.sSubscribePerson AS UserName,a.DepartName,COUNT(*) AS Cot FROM\n  view_stat_patientbaseinfo a LEFT JOIN Register r ON a.PatientId = r.sPatienId\n  GROUP BY r.sSubscribePerson,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*接待人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT\n  r.sAdmitNurseName AS UserName,a.DepartName,COUNT(*) AS Cot FROM\n  view_stat_patientbaseinfo a LEFT JOIN InjectionInfo r ON a.PatientId =\n  r.sPatientId GROUP BY r.sAdmitNurseName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*问诊人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sDoctorName\n  AS UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a\n  LEFT JOIN Inquisition r ON a.PatientId = r.sPatientId GROUP BY\n  r.sDoctorName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*审核人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sExamineName\n  AS UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a\n  LEFT JOIN Report r ON a.PatientId = r.sPatientId GROUP BY\n  r.sExamineName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*登记人*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT r.sCheckPerson\n  AS UserName,a.DepartName,COUNT(*) AS Cot FROM view_stat_patientbaseinfo a\n  LEFT JOIN Register r ON a.PatientId = r.sPatienId GROUP BY\n  r.sSubscribePerson,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									/*报告医生（人）*/\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									SELECT\n  r.sReportDoctorName AS UserName,a.DepartName,COUNT(*) AS Cot FROM\n  view_stat_patientbaseinfo a LEFT JOIN Report r ON a.PatientId = r.sPatientId\n  GROUP BY r.sReportDoctorName,a.DepartName\n								</p>\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n							<td style=\"vertical-align:top;\">\n								<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n									&nbsp;\n								</p>\n							</td>\n						</tr>\n					</tbody>\n				</table>\n			</div>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				&nbsp;\n			</p>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				&nbsp;\n			</p>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				&nbsp;\n			</p>\n			<p style=\"font-family:微软雅黑;font-size:11.0pt;\">\n				3\n			</p>\n		</div>\n	</div>\n</div>', '', '2', '7d79647a', '0', null, 'admin001', '2019-02-20 10:44:22', 'admin', '2019-02-20 11:50:32', '0', '55', '3');
INSERT INTO `hn_note` VALUES ('f354b98e', 'MyBatis 打印 SQL 语句配置', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"847\" height=\"224\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">在</span><span style=\"font-weight:bold;\"> mybaits </span><span style=\"font-weight:bold;\">配置中，添加属性（</span><span style=\"font-weight:bold;\">spring-mybatis.xml</span><span style=\"font-weight:bold;\">）</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">&lt;!-- 配置SqlSessionFactoryBean --&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;bean id=\"</span><span style=\"font-weight:bold;\">ssfb</span><span style=\"font-weight:bold;color:#FF0066;\">\"\n  class=\"</span><span style=\"font-weight:bold;\">org.mybatis.spring.SqlSessionFactoryBean</span><span style=\"font-weight:bold;color:#FF0066;\">\"&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">&lt;!-- 指定连接池 --&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;\">dataSource</span><span style=\"font-weight:bold;color:#FF0066;\">\" ref=\"</span><span style=\"font-weight:bold;\">dataSource</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">&lt;!-- 在控制台打印SQL语句 --&gt;</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">&lt;property name=\"</span><span style=\"font-weight:bold;color:#0070C0;background:yellow;\">configLocation</span><span style=\"font-weight:bold;color:#FF0066;\">\" value=\"</span><span style=\"font-weight:bold;color:#0070C0;background:yellow;\">classpath:mybatis-config.xml</span><span style=\"font-weight:bold;color:#FF0066;\">\"/&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/bean&gt;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"849\" height=\"822\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">2</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">mybatis-config.xml </span><span style=\"font-weight:bold;\">配置文件</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">&lt;?xml version=\"1.0\"\n  encoding=\"UTF-8\"?&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">&lt;!DOCTYPE configuration<span>&nbsp;&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;&nbsp;&nbsp; </span>PUBLIC\n  \"-//mybatis.org//DTD Config 3.0//EN\"<span>&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;&nbsp;&nbsp; </span>\"</span><a href=\"http://mybatis.org/dtd/mybatis-3-config.dtd\"><span style=\"font-weight:bold;\">http://mybatis.org/dtd/mybatis-3-config.dtd</span></a><span style=\"font-weight:bold;\">\"&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;configuration&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;&nbsp;&nbsp; </span>&lt;settings&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-weight:bold;color:#7F7F7F;\">&lt;!-- 打印SQL语句\n  --&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting name=\"</span><span style=\"font-weight:bold;color:#0070C0;\">logImpl</span><span style=\"font-weight:bold;color:#FF0066;\">\"\n  value=\"</span><span style=\"font-weight:bold;color:#0070C0;\">STDOUT_LOGGING</span><span style=\"font-weight:bold;color:#FF0066;\">\" /&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						&nbsp;\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;background:yellow;\">&lt;!--\n  以下配置可以不要</span><span style=\"font-weight:bold;background:yellow;\"> </span><span style=\"font-weight:bold;background:yellow;\">--&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"color:#7F7F7F;\">&lt;!--\n  全局映射器启用缓存 --&gt;<span>&nbsp; </span></span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"cacheEnabled\" value=\"true\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 查询时,关闭关联对象及时加载以提高性能\n  --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"lazyLoadingEnabled\" value=\"false\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!--\n  设置关联对象加载的形态,此处为按需加载字段(加载字段由SQL指定),不会加载关联表的所有字段,以提高性能 --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"aggressiveLazyLoading\" value=\"false\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 对于位置的SQL查询,允许返回不同的结果集以达到通用的效果\n  --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"multipleResultSetsEnabled\" value=\"true\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 允许使用列标签代替列明 --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"useColumnLabel\" value=\"true\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 允许使用自定义的主键值(比如由程序生成的UUID\n  32位编码作为键值), 数据表的pk生成策略将被覆盖 --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"useGeneratedKeys\" value=\"true\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 给予被嵌套的resultMap以字段-属性的映射支持\n  --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"autoMappingBehavior\" value=\"PARTIAL\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 对于批量更新操作缓存SQL以提高性能\n  --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"defaultExecutorType\" value=\"REUSE\" /&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;!-- 数据库超过25000秒仍未响应则超时\n  --&gt;<span>&nbsp; </span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>&lt;setting\n  name=\"defaultStatementTimeout\" value=\"25000\" /&gt;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\"><span>&nbsp;&nbsp;&nbsp; </span>&lt;/settings&gt;</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">&lt;/configuration&gt;</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', 'be4925e8', '0', null, 'admin001', '2019-02-14 12:57:17', null, null, '1', '96', '1');
INSERT INTO `hn_note` VALUES ('f41ea145', 'MyBatis 通用Mapper自定义SQL', '<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#E36C09;\">\n	<span style=\"font-weight:bold;\">在实体类</span><span style=\"font-weight:bold;\">Dao</span><span style=\"font-weight:bold;\">接口中，创建新方法，在方法上使用注解，注解的值填写</span><span style=\"font-weight:bold;\">sql</span><span style=\"font-weight:bold;\">语句</span><span style=\"font-weight:bold;\"> </span><span style=\"font-weight:bold;\">，如下：</span> \n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"922\" height=\"626\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						<span style=\"font-weight:bold;\">sql</span><span style=\"font-weight:bold;\">类型</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;text-align:center;\">\n						<span style=\"font-weight:bold;\">例子</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">select</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:14.0pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">方法一：</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">直接在接口方法中添加</span><span style=\"font-weight:bold;\">sql</span><span style=\"font-weight:bold;\">语句</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">public interface </span><span style=\"font-weight:bold;color:#FF0066;\">UserDao</span><span style=\"font-weight:bold;\"> extends</span><span style=\"font-weight:bold;color:#FF0066;\"> CommonDao&lt;</span><span style=\"font-weight:bold;color:#0070C0;\">User</span><span style=\"font-weight:bold;color:#FF0066;\">&gt;</span><span style=\"font-weight:bold;\">{</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@Select(\"</span><span style=\"font-weight:bold;color:#00B0F0;background:yellow;\">select * from H_USER where rowguid=\'</span><span style=\"font-weight:bold;color:#00B0F0;background:yellow;\">111</span><span style=\"font-weight:bold;color:#00B0F0;background:yellow;\">\'</span><span style=\"font-weight:bold;color:#FF0066;\">\")</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">public </span><span style=\"font-weight:bold;color:#FF0066;\">List&lt;</span><span style=\"font-weight:bold;color:#0070C0;\">User</span><span style=\"font-weight:bold;color:#FF0066;\">&gt;</span><span style=\"font-weight:bold;\"> selectUser();</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">}</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:14.0pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">方法二：（通用方法）</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">//1</span><span style=\"font-weight:bold;\">）在最顶层接口添加方法及注解，使用</span><span style=\"font-weight:bold;\"> ${} </span><span style=\"font-weight:bold;\">号获取</span><span style=\"font-weight:bold;\">sql</span><span style=\"font-weight:bold;\">语句</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">//2</span><span style=\"font-weight:bold;\">）在方法中传入参数：</span><span style=\"font-weight:bold;\">sql</span><span style=\"font-weight:bold;\">语句</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:red;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">注：参数中必须使用</span><span style=\"font-weight:bold;\"> @Param </span><span style=\"font-weight:bold;\">声明参数名称</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:red;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">接口方法</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@Select(\"</span><span style=\"font-weight:bold;color:#FF0066;background:yellow;\">${</span><span style=\"font-weight:bold;color:#0070C0;background:yellow;\">sql</span><span style=\"font-weight:bold;color:#FF0066;background:yellow;\">}</span><span style=\"font-weight:bold;color:#FF0066;\">\")</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">public </span><span style=\"font-weight:bold;color:#FF0066;\">List&lt;</span><span style=\"font-weight:bold;color:#0070C0;\">User</span><span style=\"font-weight:bold;color:#FF0066;\">&gt;</span><span style=\"font-weight:bold;\"> select</span><span style=\"font-weight:bold;\">Sql(</span><span style=\"font-weight:bold;color:#FF0066;background:yellow;\">@Param(\"</span><span style=\"font-weight:bold;color:#0070C0;background:yellow;\">sql</span><span style=\"font-weight:bold;color:#FF0066;background:yellow;\">\")</span><span style=\"font-weight:bold;\">String </span><span style=\"font-weight:bold;color:#0070C0;\">sql</span><span style=\"font-weight:bold;\">);</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#7F7F7F;\">\n						<span style=\"font-weight:bold;\">//</span><span style=\"font-weight:bold;\">使用方法</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">String </span><span style=\"font-weight:bold;color:#0070C0;\">sql</span><span style=\"font-weight:bold;\"> = \"</span><span style=\"font-weight:bold;color:#0070C0;\">select * from H_USER where\n  rowguid=\'e96c40034856444e9998e777fd5ce698\'</span><span style=\"font-weight:bold;\">\";</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;\">list = userService.</span><span style=\"font-weight:bold;color:#FF0066;\">select</span><span style=\"font-weight:bold;color:#FF0066;\">Sql</span><span style=\"font-weight:bold;\">(</span><span style=\"font-weight:bold;color:#0070C0;\">sql</span><span style=\"font-weight:bold;\">);</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">update</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Update</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">insert</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						<span style=\"font-weight:bold;color:#FF0066;\">@Insert(</span><span style=\"font-weight:bold;color:#00B0F0;background:yellow;\">\"insert into table2 (name) values(#{name})\"</span><span style=\"font-weight:bold;color:#FF0066;\">)</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">delete</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Delete</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>', '', '2', 'be4925e8', '0', null, 'admin001', '2019-02-14 12:58:32', null, null, '1', '94', '1');
INSERT INTO `hn_note` VALUES ('f934789a', 'Centos7 telnet安装与使用', '<p style=\"font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;font-family:微软雅黑;\">先检查是否安装</span><span style=\"font-weight:bold;font-family:Calibri;\">:</span><span style=\"font-weight:bold;font-family:微软雅黑;\">&nbsp;</span><span style=\"font-weight:bold;font-family:Calibri;\">telnet-server</span><span style=\"font-weight:bold;font-family:微软雅黑;\">和</span><span style=\"font-weight:bold;font-family:Calibri;\">xinetd</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	<span style=\"font-weight:bold;\">rpm -qa telnet-server</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	<span style=\"font-weight:bold;\">rpm -qa xinetd</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;\">如果没有安装，则安装：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	yum list\n|grep telnet\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	-------&gt;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>telnet.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> 1:0.17-60.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>@base </span><span>&nbsp;</span><span> </span><span>&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>dcap-tunnel-telnet.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> 2.47.10-5.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>epel </span><span>&nbsp;</span><span> </span><span>&nbsp;&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>libguac-client-telnet.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>1:0.9.12-1.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> epel </span><span>&nbsp;</span><span> </span><span>&nbsp;&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>libtelnet.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>0.21-5.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> epel </span><span>&nbsp;</span><span> </span><span>&nbsp;&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>libtelnet-devel.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>0.21-5.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> epel </span><span>&nbsp;</span><span> </span><span>&nbsp;&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>libtelnet-utils.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>0.21-5.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> epel </span><span>&nbsp;</span><span> </span><span>&nbsp;&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>telnet-server.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>1:0.17-60.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>base</span><span>&nbsp;</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	yum list\n|grep xinetd\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	--------&gt;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>xinetd.x86_64\n&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> 2:2.3.15-13.el7 </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span> </span><span>&nbsp;</span><span>base</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;\">执行安装：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	yum -y\ninstall telnet-server.x86_64\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	yum -y\ninstall telnet.x86_64\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	yum -y\ninstall xinetd.x86_64\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;\">设置开机启动：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	systemctl\nenable xinetd.service\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	systemctl\nenable telnet.socket\n</p>\n<p style=\"font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;font-family:微软雅黑;\">开启</span><span style=\"font-weight:bold;font-family:Calibri;\">service</span><span style=\"font-weight:bold;font-family:微软雅黑;\">：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	systemctl\nstart telnet.socket\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	systemctl\nstart xinetd\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;\">开启防火墙端口：</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	firewall-cmd\n--permanent --add-port=23/tcp\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	firewall-cmd --reload\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>tail\n-f&nbsp;</span><span>/var/log/secure</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	报错：\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	<span>Mar 28\n18:09:47 localhost login: PAM 2 more authentication failures; logname= uid=0\neuid=0 tty=pts/1 ruser= rhost=::ffff:10.102.45.38 &nbsp;</span><span>user=root</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#E36C09;\">\n	<span>vi&nbsp;</span><span>/etc/securetty</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n	<span style=\"font-weight:bold;\">添加</span> \n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	pts/0\n</p>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	pts/1\n</p>', '', '2', 'f5e3e654', '0', null, 'admin001', '2019-02-19 18:23:29', null, null, '1', '94', '1');
INSERT INTO `hn_note` VALUES ('fb077ea9', 'SpringMVC 综合（拦截器）', '<div>\n	<table style=\"border-collapse:collapse;\" width=\"924\" height=\"36\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">注解</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">过虑器，编码</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">参数</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">异常</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">拦截器</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">自定义拦截器（注解）</span> \n					</p>\n				</td>\n			</tr>\n		</tbody>\n	</table>\n</div>\n<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n	&nbsp;\n</p>\n<div>\n	<table style=\"border-collapse:collapse;\" width=\"924\" height=\"2071\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n		<tbody>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">1</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#366092;\">\n						<span style=\"font-weight:bold;\">Spring </span><span style=\"font-weight:bold;\">中相关注解</span> \n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:12.0pt;color:#E36C09;\">\n						<span style=\"font-weight:bold;\">注解</span> \n					</p>\n				</td>\n			</tr>\n			<tr>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n				</td>\n				<td style=\"vertical-align:top;\">\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Controller</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						注解为控制类\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@RequestMapping</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						注解为请求路径处理方法\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						也可以使用在类上，表示父路径\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						参数：请求参数地址\n					</p>\n					<div>\n						<table style=\"border-collapse:collapse;\" width=\"810\" height=\"137\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n							<tbody>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">例：</span> \n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											@RequestMapping(\"/getList\")\n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											public void getList(){\n										</p>\n										<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											...\n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											}\n										</p>\n									</td>\n								</tr>\n							</tbody>\n						</table>\n					</div>\n					<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Service</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						业务层\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Repository</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						持久层\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@RequestParam</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						注解为参数\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						匹配参数与请求参数\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@ExceptionHandler</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						异常处理\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Component</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						持久层\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						声明这是一个组件（可以由spring管理）\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@PostConstruct</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						注解初始化方法\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@PreDestory</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						注解销毁方法\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Scope</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						设置作用域\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						单例：singleton（默认值）\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>多例：</span><span>prototype</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Lazy</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						设置延迟加载\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>延迟加载：</span><span>true</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Resource</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						依赖注入\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>Setter </span><span>注入推荐使用本注解</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>参数：指定被注入的</span><span> bean </span><span>的</span><span> id</span> \n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Autowired</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						依赖注入\n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						<span>构造器注入推荐使用本注解，也可以使用为</span><span> Setter </span><span>注入</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						&nbsp;\n					</p>\n					<p style=\"font-family:微软雅黑;font-size:10.5pt;color:#FF0066;\">\n						<span style=\"font-weight:bold;\">@Qualifier</span> \n					</p>\n					<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;\">\n						在参数中，指定被注入的参数的类型\n					</p>\n					<div>\n						<table style=\"border-collapse:collapse;\" width=\"774\" height=\"137\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n							<tbody>\n								<tr>\n									<td style=\"vertical-align:top;\">\n										<p style=\"font-family:微软雅黑;font-size:10.5pt;\">\n											<span style=\"font-weight:bold;\">例一：</span> \n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											@Autowired\n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											public void set(@Qualifier(\"cp\")\n    Computer cp) {\n										</p>\n										<p style=\"margin-left:.75in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											...\n										</p>\n										<p style=\"margin-left:.375in;font-family:微软雅黑;font-size:10.5pt;color:#00B050;\">\n											}\n										</p>\n									</